/** 
 * Funzione helper per ottenere un Polygon da roughness
 */
export function getRoughnessPolygon(level: number, scale = 128): Polygon {
  const flat = roughnessStamps[level];
  if (!flat) throw new Error(`Roughness ${level} non trovata`);
  const ring: Vec2[] = [];
  for (let i = 0; i < flat.length; i += 2) {
    ring.push([flat[i] * scale, flat[i + 1] * scale]);
  }
  return [ring];
}
